	(73,'wx_AppSecret',1,'AppSecret(应用密钥)',6,'','AppSecret(应用密钥)</br>\r\n<code>⚠️ 修改此项，需要重启服务.</code>',1483601985777,1460919187704,1,'4e24b6dcebd27caa17d1866cfd4aca49',4),
	(74,'wx_Token',1,'Token(令牌)',6,'','Token(令牌)</br>\r\n<code>⚠️ 修改此项，需要重启服务.</code>',1483602109838,1460919336290,1,'cmswing',5),
	(75,'IS_QQ_LOGIN',4,'是否开启QQ登陆',7,'0:关闭\r\n1:开启','开启后请配置下面的APP ID和APP KEY ，<a href=\"http://connect.qq.com/\" class = \"text-info\" target=\"_blank\">APP ID和APP KEY申请</a><br>\r\n回调地址：<code>http://www.abc.com/uc/qq/index</code></br>\r\n申请接口的是时候需要填写回调地址，你把www.abc.com替换成你网站的域名 如：http://www.cmswing.com/uc/qq/index',1474784802951,1461067751607,1,'1',1),
	(76,'QQ_APPID',1,'QQ  APP ID',7,'','在QQ互联申请到的 APP ID ,填写到此处。',1461072196964,1461067916788,1,'101309492',2),
	(77,'QQ_APPKEY',1,'QQ APP KEY',7,'','在QQ互联申请到的 APP KEY ,填写到此处。',1461072212437,1461068049281,1,'98c05261f58f2fe30478bb168d143113',2),
	(78,'QQ_VALIDATION',1,'QQ验证码',7,'','申请QQ登录应用的时候，会提供给你一段代码进行网站验证,请填写到此处。',1461078426145,1461078345202,1,'<meta property=\"qc:admins\" content=\"1271366777635371676375\" />',4),
	(79,'IS_QINIU',4,'开启七牛云储存',8,'0:关闭\r\n1:开启','开启前先填写七牛的AccessKey/SecretKey，<a href=\"http://www.qiniu.com/\" target=\"_blank\" class=\"text-info\">申请地址</a>',1461171840594,1461171178703,1,'1',0),
	(80,'QINIU_AK',1,'七牛 AK',8,'','七牛云储存 AccessKey',0,1461171314237,1,'OJD9JCXudNtPwz_bKrtdnP2uTd5BVGvEJxaiUB24',1),
	(81,'QINIU_SK',1,'七牛 SK',8,'','七牛云储存 SK',0,1461171443001,1,'_Dmewmycq994GcYxG4N3WvOX0ED-5eUeeXvoOYcE',2),
	(82,'QINIU_BUCKET',1,'空间名',8,'','在七牛后台创建的对象储存名称',0,1461171792507,1,'cmswing',4),
	(83,'QINIU_DOMAIN_NAME',1,'七牛空间域名',8,'','七牛空间域名如：	7xt6v5.com1.z0.glb.clouddn.com 不带http://',0,1461175719237,1,'data.cmswing.com',5),
	(84,'CY_APPID',1,'APP ID',9,'',' 畅言APP ID  <a href=\"http://changyan.kuaizhan.com/\" target=\"_blank\" class=\"text-info\">点击申请</a>',1463639830539,1463639695026,1,'cysoIp9AH',0),
	(85,'CY_APPKEY',1,'APP KEY',9,'',' 畅言 APP KEY  <a href=\"http://changyan.kuaizhan.com/\" target=\"_blank\" class=\"text-info\">点击申请</a>\r\n <h4>PC端单点登录配置参数</h4>\r\n        <p>\r\n        登录行为(打开新页面登录):<code>http://网站域名/uc/changyan/signin</code>\r\n        </p>\r\n        <p>\r\n            获取用户信息接口URL:<code>http://网站域名/uc/changyan/getuserinfo</code>\r\n        </p>\r\n        <p>\r\n            登录接口URL:<code>http://网站域名/uc/changyan/login</code>\r\n        </p>\r\n        <p>\r\n            用户退出接口URL:<code>http://网站域名/uc/public/logout</code>\r\n        </p>',1474674585355,1463639950200,1,'e2066f1e6fc3b33e5f7f31ba1365bf63',1),
	(86,'WEB_TONGJI',2,'统计代码',1,'','统计代码',1464687748400,1464687707759,1,'<script>\r\nvar _hmt = _hmt || [];\r\n(function() {\r\n  var hm = document.createElement(\"script\");\r\n  hm.src = \"//hm.baidu.com/hm.js?db1838bfd0b81ebff469f5ef62a0621a\";\r\n  var s = document.getElementsByTagName(\"script\")[0]; \r\n  s.parentNode.insertBefore(hm, s);\r\n})();\r\n</script>',10),
	(87,'GEETEST_ID',1,'ID (网页端)',10,'','从<a class=\"text-info\" href=\"http://user.geetest.com/\" target=\"_blank\">验证后台</a> 注册账号,添加验证，可以获取一组当前验证的ID (网页端)',1473017424630,1468474179391,1,'4dad8be53801fa4e2e50c1be078e2187',1),
	(88,'GEETEST_KEY',1,'KEY (网页端)',10,'','从<a class=\"text-info\" href=\"http://user.geetest.com/\" target=\"_blank\">验证后台</a> 注册账号,添加验证，可以获取一组当前验证的KEY (网页端)',1473017439964,1468474373867,1,'ae68a05dc013d21cad068a7f4271eca1',2),
	(89,'GEETEST_IS_LOGIN',4,'前台登录验证',10,'0:关闭\r\n1:开启','是否开启、关闭前台登录验证码，默认关闭，开启此选项必须 移动端和网页端的ID和KEY都要填入。请在极验后台分别创建。',1473017807773,1468475188387,1,'1',5),
	(90,'GEETEST_IS_ADMLOGIN',4,'后台登录验证',10,'0:关闭\r\n1:开启','是否开启、关闭后台登录验证码，默认关闭',1473017608058,1468475293815,1,'1',6),
	(91,'USER_ALLOW_SMSREGISTER',4,'注册方式',3,'0:传统注册\r\n1:短信注册','短信注册，网站必须开启短信功能。',1468487539814,1468487490603,1,'0',1),
	(92,'VIDEOJJ_appkey',1,'APPKey',11,'','必填，请在控制台查看应用标识  <a href=\"http://videojj.com/\" target=\"_blank\" class=\"text-info\">videojj申请地址</a>',1468514535175,1468514516660,1,'VJa72yR7W',1),
	(93,'VIDEOJJ_videoStart',4,'跳过片头',11,'0:是\r\n1:否','选填，跳过片头，默认为0',0,1468515054739,1,'0',2),
	(94,'VIDEOJJ_videoEnd',4,'跳过片尾',11,'0:是\r\n1:否','选填，跳过片尾，默认为0',0,1468515144580,1,'0',3),
	(95,'VIDEOJJ_skinSelect',4,'播放器皮肤',11,'0:科技\r\n1:经典\r\n3:时尚','选填，播放器皮肤，可选0、1、2，默认为0',0,1468515265463,1,'0',4),
	(96,'VIDEOJJ_autoplay',4,'是否自动播放',11,'false:否\r\ntrue:是','选填，是否自动播放，默认为false',0,1468515466792,1,'true',5),
	(97,'VIDEOJJ_rightHand',4,'右键菜单',11,'true:开启\r\nfalse:关闭','选填，是否开启右键菜单，默认为false',0,1468515661958,1,'true',6),
	(98,'VIDEOJJ_autoFormat',4,'选择清晰度',11,'false:不自动选择\r\ntrue:自动选择','是否自动选择最高清晰度，默认为false',0,1468515906079,1,'false',7),
	(99,'VIDEOJJ_bubble',4,'云泡功能:',11,'true:开启\r\nfalse:关闭','是否开启云泡功能，默认为true',0,1468516106128,1,'true',9),
	(100,'VIDEOJJ_jumpStep',0,'快进快退时间设置',11,'','左右方向键快退快进的时间,单位‘秒’',0,1468516226215,1,'10',10),
	(101,'VIDEOJJ_tagShow',4,'热点默认显示功能',11,'true:显示\r\nfalse:隐藏','云链是否显示，默认为false',1468518689804,1468516447326,1,'true',11),
	(102,'VIDEOJJ_tagTrack',4,'热点默认跟踪功能',11,'true:跟踪\r\nfalse:不跟踪','云链是否跟踪，默认为false',0,1468516671802,1,'true',12),
	(103,'VIDEOJJ_tagDuration',0,'热点显示时间',11,'','云链显示时间，默认为5秒',0,1468516792249,1,'5',14),
	(104,'VIDEOJJ_tagFontSize',4,'热点字号',11,'12:12px\r\n14:14px\r\n16:16px\r\n18:18px\r\n20:20px\r\n22:22px\r\n24:24px\r\n26:26px\r\n28:28px','云链文字大小，默认为16像素',0,1468517052143,1,'16',13),
	(105,'SMS_TYPE',4,'短信通道',12,'1:阿里大鱼','阿里大鱼 <a href=\"http://www.alidayu.com/\" target=\"_blank\" class=\"text-info\">申请地址</a>  ≤4.5分/条',0,1468557817087,1,'1',1),
	(106,'SMS_Key',1,'APP证书',12,'','在相应的短信通道后台获取。<br>\r\n阿里大鱼:App Key|App Secret',1468580594000,1468558104133,1,'23381127|3bb09aa73066dc1c469d6e7a9d909731',2),
	(107,'SMS_qianming',1,'短信签名',12,'','在相应的短信后台获取</br>\r\n短信示例：【阿里大鱼】 验证码${number}，您正进行支付宝的身份验证，打死不告诉别人！ </br>\r\n短信签名：阿里大鱼 </br>\r\n短信模板: 验证码${number}，您正进行支付宝的身份验证，打死不告诉别人！',1468581953656,1468558176158,1,'酷翼cms',3),
	(108,'SMS_zhuce',1,'用户注册验证码模版ID',12,'','在短信渠道后台申请</br>\r\n实例：验证码${code}，您正在注册成为${product}用户，感谢您的支持！',1468583238487,1468582222696,1,'SMS_10281005',5),
	(109,'SMS_product',1,'变量${product}',12,'','设置模版的时候填入的值，会替换${product}',0,1468583227214,1,'CmsWing',4),
	(110,'GEETEST_ID_M',1,'ID (移动端)',10,'','从<a class=\"text-info\" href=\"http://user.geetest.com/\" target=\"_blank\">验证后台</a> 注册账号,添加验证，可以获取一组当前验证的ID (移动端)',0,1473017565745,1,'61844714815758b8ea27c74681c3eb06',3),
	(111,'GEETEST_KEY_M',1,'KEY (移动端)',10,'','从<a class=\"text-info\" href=\"http://user.geetest.com/\" target=\"_blank\">验证后台</a> 注册账号,添加验证，可以获取一组当前验证的KEY (移动端)',1473017708120,1473017680238,1,'b8bbf948f416c2c43927334f31075c76',4),
	(112,'IS_SINA_LOGIN',4,'是否开启新浪微博登陆',7,'0:关闭\r\n1:开启','开启后请配置下面的APP ID和APP KEY ，<a href=\"http://open.weibo.com/\" class = \"text-info\" target=\"_blank\">APP ID和APP KEY申请</a><br>\r\n回调地址：<code>http://www.abc.com/uc/sina/index</code></br>\r\n申请接口的是时候需要填写回调地址，你把www.abc.com替换成你网站的域名 如：http://www.cmswing.com/uc/sina/index',0,1481700902627,1,'1',5),
	(113,'SINA_APPKEY',1,'SINA App Key',7,'','在微博开放平台申请到的 App Key ,填写到此处。',0,1481701099061,1,'3868482864',6),
	(114,'SINA_APPSECRET',1,'SINA App Secret',7,'','在微博开放平台申请到的 App Secret ,填写到此处。',0,1481701308499,1,'ec0c3346d244d91c6e0189b4448fb326',7),
	(115,'SINA_VALIDATION',1,'SINA验证码',7,'','申请微博登录应用添加新网站的时候，会提供给你一段代码进行网站验证,请填写到此处。',0,1481701478702,1,'<meta property=\"wb:webmaster\" content=\"bc4f6c6952e7529b\" />',8),
	(116,'SYS_PRIV_ROLE',3,'系统模型角色权限',3,'','格式<code>权限名(英文):说明</code>',1483457388320,1483457345106,1,'init:查看\r\nadd:添加\r\nedit:编辑\r\ndelete:删除\r\nexamine:审核\r\ndisable:禁用\r\nlistorder:排序\r\npush:推送\r\nmove:移动',3),
	(117,'SYS_PRIV_GROUP',3,'系统模型会员组权限',3,'','格式 <code>权限名(英文):说明</code>',1483461870509,1483457987068,1,'visit:访问\r\nadd:投稿\r\naddexa:投稿审核\r\neditexa:编辑审核',4),
	(118,'SP_PRIV_ROLE',3,'单页模型角色权限',3,'','格式<code>权限名(英文):说明</code>',0,1483461697113,1,'init:查看\r\nadd:添加\r\nedit:编辑\r\ndelete:删除',5),
	(119,'SP_PRIV_GROUP',3,'单页模型会员组权限',3,'','格式 <code>权限名(英文):说明</code>',1483461898763,1483461790337,1,'visit:访问',6),
	(120,'wx_name',1,'公众号名称',6,'','微信公共账号名称',0,1483602348252,1,'北京博思众合科技有限责任公司',1),
